import React,{ useState } from 'react';
import './index.css';
import AOS from "aos";
import "aos/dist/aos.css";
import List from './List';
AOS.init();

const App = () =>{

    const [inputList,setInputList]= useState("");
    const [Items,setItems]=useState([]);

    const itemEvent=(event)=>
    {
       setInputList(event.target.value);
    };
 
    function todoList() {
        setItems((oldItems) => {
            return [...oldItems, inputList];
        });
        setInputList("");

    }
    const deleteItems= (id) =>{
    setItems((oldItems) =>{
        return oldItems.filter((arrElem,index) =>{
            return index !== id;
        });
    })
        

    };
    let currTime=new Date();
currTime=currTime.getHours();
let  greeting=" ";
const cssstyle={ };
if(currTime<12)
{
 greeting="good morning";
 cssstyle.color='red';
}
else if(currTime>=12 && currTime<=16)
{
    greeting="good afternoon";
    cssstyle.color='yellow';
}
else if(currTime>=17 && currTime<20)
{
    greeting="good evening";
   cssstyle.color='blue';
}
else
{
    greeting="good night";
    cssstyle.color='black';
}
return(<>
<div className="x" data-aos="fade-right" data-aos-duration="500">
<h1 className="greet">Hello Mam,
<span style={cssstyle}>{greeting}</span>
</h1>
</div>
<div className="container">
	
<div  className="card  card-shadow mb-3 w-75 mx-auto " data-aos="fade-right" data-aos-duration="3000">
  <div className="card-body">
    <br/>
    <h1 className="mt-5 text-center" data-aos="fade-up" data-aos-duration="2000" > ToDo List</h1>
    <br/>
    
    <input className="mx-auto w-60" type="text" placeholder = "Add todos" 
    value={inputList} onChange={itemEvent}/>
    <h1 style={{float:'right',paddingRight:'30px'}}><i class="bi bi-plus-square-fill" onClick={todoList }> </i></h1>

      
      <ol>
         
      
     { Items.map( (todos,index)=>{
          return <List 
          key={index} 
          text={todos} 
          id={index}
            onSelect={deleteItems}  
          />
      })
     } 
      </ol>
  </div>
</div>
</div>
</>);
};
export default App;
