                         <input class="mt-2" type="text" 
                         name="item1"
                         onChange={Input} value={item1} placeholder="enter your first name" /> <br></br>

                         <input class="mt-3" type="text" 
                         name="lastName"
                         onChange={Input} value={lastName} placeholder="enter your last name" /><br></br>

                         <button class="mt-3 " type="submit" > Submit me 👍 </button>
                    </form>
               </center>
          </>
     )
}

export default Apps;
