import React, { useState } from 'react';

const Apps = () => {
     const [item1, setItem1] = useState("");

     const [lastName, setlastName] = useState("");
     const [item2, setItem2] = useState("");
     const [full, setFull] = useState("");

     const Input1 = (event) => {
          setItem1(event.target.value);
     };
     const Input2 = (event) => {
          setlastName(event.target.value);
     };
     const onSubmits = (event) => {
          event.preventDefault();
          setItem2(item1);
          setFull(lastName);
     };

     return (
          <>
               <center>
                    <form onSubmit={onSubmits} >
                         <h1 class="mt-5">Hello {item2} {full} </h1>
                         <input class="mt-2" type="text" onChange={Input1} value={item1} placeholder="enter your first name" /> <br></br>

                         <input class="mt-3" type="text" onChange={Input2} value={lastName} placeholder="enter your last name" /><br></br>

                         <button class="mt-3 " type="submit" > Submit me üëç </button>
                    </form>
               </center>
          </>
     )
}

export default Apps;
