import React,{useState} from 'react';

const Apps= () =>{
     const[item1,setItem1]=useState(""); 
     const[item2,setItem2]=useState(""); 
     
     const Fname =(event) =>
     {
        setItem1(event.target.value);
     };
     const Lname =(event) =>
     {
        setItem2(event.target.value);
     };
     const onSubmits =(event) =>
     {
          event.preventDefault();
           setItem1(event.target.value);
          setItem2(event.target.value);
     };

return(
<>
<center>
<form onSubmit={onSubmits} >
<h1 class="mt-5">Hello {item1} {item2} </h1>
<input class="mt-2" type="text" onChange={Fname} value={item1} placeholder="enter your first name"/> <br></br>
<input class="mt-3" type="text" onChange={Lname} value={item2} placeholder="enter your last name"/><br></br>
<button class="mt-3 " type="submit" > Submit me 👍 </button>
</form>
</center>
</>
)
}

export default Apps;
