import React, { useState, useEffect } from 'react';
import './Sorting.css';

const Sorting = () => {
  const [array1, setArray1] = useState([]);

  const resetArray = () => {
    const a = [];
    for (let i = 0; i < 310; i++) {
      a.push(RandomInt(5, 720));
    }
    setArray1(a);
  }

  useEffect(() => {
    resetArray();
  }, []);

  return (
    <>
      <div className="array-container">
        {
          array1.map((value, index) => (
            <div className="arrayBar"
              key={index}
              style={{ height: `${value}px` }}
            >

            </div>
          ))
        }
        <center>
        <button className="btn btn-lg btn-dark" onClick={resetArray}>Reset Array</button>
        <button className="btn btn-lg btn-dark" onClick={mergeSort}>Merge Sort</button>
        <button className="btn btn-lg btn-dark" onClick={quickSort}>Quick New Sort</button>
        <button className="btn btn-lg btn-dark" onClick={heapSort}>Heap New Sort</button>
        <button className="btn btn-lg btn-dark" onClick={bubbleSort}>Bubble New Sort</button>

        </center>
      </div>
    </>
  );
}
function RandomInt(min, max) {
  return Math.floor(Math.random() * (max - min + 1) + min);
}

export default Sorting;
