import React from 'react';
import './index.css';
import AOS from "aos";
import "aos/dist/aos.css";
AOS.init();

function add(a,b)
{
return a+b;
}
function sub(a,b)
{
return a-b;
}
function mul(a,b){
return a* b;
}
function div(a,b)
{ let div=a/b;
    div = div.toFixed(2);
return div;
}

function App(){
let currTime=new Date();
currTime=currTime.getHours();
let  greeting=" ";
const cssstyle={ };
if(currTime<12)
{
 greeting="good morning";
 cssstyle.color='red';
}
else if(currTime>=12 && currTime<=16)
{
    greeting="good afternoon";
    cssstyle.color='yellow';
}
else if(currTime>=17 && currTime<20)
{
    greeting="good evening";
   cssstyle.color='blue';
}
else
{
    greeting="good night";
    cssstyle.color='black';
}
return(<>
<div className="x" data-aos="fade-right" data-aos-duration="2500">
<h1>Hello Mam,
<span style={cssstyle}>{greeting}</span>
</h1>
</div>

<ul>
  <li>Sum of two no is {add(2,3)}</li>
  <li>Sub of two no is {sub(12,1)}</li>  
  <li>mult of two no is {mul(2,3)}</li>
  <li>div of two no is {div(12,5)}</li>
</ul>
</>);
}
export default App;
