import React,{ useState } from 'react';
import './index.css';
import AOS from "aos";
import "aos/dist/aos.css";
import List from './List';
AOS.init();

const App = () =>{

    const [inputList,setInputList]= useState("");
    const [Items,setItems]=useState([]);

    const itemEvent=(event)=>
    {
       setInputList(event.target.value);
    };
 
    function todoList() {
        setItems((oldItems) => {
            return [...oldItems, inputList];
        });
        setInputList("");

    }
    const deleteItems= (id) =>{
    setItems((oldItems) =>{
        return oldItems.filter((arrElem,index) =>{
            return index !== id;
        });
    })
        

    };
    let currTime=new Date();
currTime=currTime.getHours();
let  greeting=" ";
const cssstyle={ };
if(currTime<12)
{
 greeting="good morning";
 cssstyle.color='red';
}
else if(currTime>=12 && currTime<=16)
{
    greeting="good afternoon";
    cssstyle.color='yellow';
}
else if(currTime>=17 && currTime<20)
{
    greeting="good evening";
   cssstyle.color='blue';
}
else
{
    greeting="good night";
    cssstyle.color='black';
}
return(<>
<div className="x" data-aos="fade-right" data-aos-duration="2500">
<h1 className="greet">Hello Mam,
<span style={cssstyle}>{greeting}</span>
</h1>
</div>

<div style={{justifyContent:'center'}} className="card">
  <div className="card-body">
    <br/>
    <h1> ToDo List</h1>
    <br/>
    <input type="text" placeholder = "Add todos" value={inputList} onChange={itemEvent}/>
    <button onClick={todoList}> + </button>
      
      <ol>
         
      
     { Items.map( (todos,index)=>{
          return <List 
          key={index} 
          text={todos} 
          id={index}
            onSelect={deleteItems}  
          />
      })
     } 
      </ol>
  </div>
</div>

</>);
};
export default App;
